            result = String(Res.body());
            try {
                JSON.parse(result);
            } catch (parse_err) {
                subStr = result.substring(3, result.length - 4);
                result = java.createSymmetricCrypto("DES/CBC/PKCS5Padding", "6CB1E21E", "1F0FB845").decryptStr(subStr);
            }
            cipher = java.createSymmetricCrypto("DESede/CBC/PKCS5Padding", "OW84U8Eerdb99rtsTXWSILDO", "SK8bncVu");
            result = String(result)
                .replace(/[^"]*[{}]{6,}([^"]+)/g, ($0, $1) => String(cipher.decryptStr($1))
