@js:
burl="https://api-lf.fanqiesdk.com";
sort=[];
push=(title,url,type1,type2)=>sort.push({
		title: title,
		url: url=url!=null?burl+url:"",
		style: {
				layout_flexGrow: type1,
				layout_flexBasisPercent: type2
			}
	});

	$$=[["推荐","10"],["完结","11"],["热搜","12"],["黑马","13"],["爆更","15"]]	;
[["男频","1"],["女频","0"],["出版","2"]].map([title,id]=>{
	push('🔖'+title+'榜单🔖', null, 1, 1);
		$$.map([title,uri]=>{			
		  	url= `/api/novel/channel/homepage/rank/rank_list/v2/?aid=13&limit=30&offset={{(page-1)*30}}&side_type=${uri}&type=${id}`;
				push(title, url, 1, 0.25);			
			});
	});

category_url="https://api-hl.fanqiesdk.com/api/novel/channel/homepage/new_category/page/data/v1/?aid=13";
data=JSON.parse(java.ajax(category_url)).data;

  function json(data) {
     boy=data.boy_category;
     girl=data.girl_category;
     publish=data.publish_category;
     json=[["男频",boy],["女频",girl],["出版",publish]];
     return JSON.parse(JSON.stringify(json));
   }	

		json(data).map([tit1,category]=>{
	[["连载","1"],["完结","0"]].map([tit2,id]=>{  
	    push('🔖'+tit1+'🍁'+tit2+'🔖', null, 1, 1);
     category.map(($,index)=>{
     	index++;
	    title=$.category_name;
     	cid=$.category_id;
	url= `/api/novel/channel/homepage/new_category/book_list/v1/?aid=1967&limit=10&offset={{(page-1)*10}\}&category_id=${cid}&creation_status=${id}&gender=1`;
  	if(index <= category.length - category.length%3)
			 {	push(title, url, 1, 0.25);}
				else{ push(title, url, 0, 0.3);}
          });
      });
  });
JSON.stringify(sort);

